import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import {Table} from 'react-bootstrap';


class App extends Component {
	constructor(props) {
		super(props);
		this.state={
			repository: null,
			language: "python",
			data: null,
			destaque: null
		}
	this.handleTextChange = this.handleTextChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
	this.handleSelectChange = this.handleSelectChange.bind(this);
	this.renderResults = this.renderResults.bind(this);
	this.returnTable = this.returnTable.bind(this)
	this.onSavePressed = this.onSavePressed.bind(this)

	}


	handleSubmit(event){
		console.log(this.state.repository)
		let URL = "https://api.github.com/search/repositories?q="
		let keywords = this.state.repository
		let language = "&language:" + this.state.language
		console.log("https://api.github.com/search/repositories?q="+keywords +language)
		fetch(URL + keywords + language)
			.then(res => res.json())
				.then(json => this.setState({ data: json }));
		event.preventDefault()
	}

	handleSelectChange(value){
		this.setState({language: value})
	}

	handleTextChange(value){
		let words = value.split(" ")
		value = words.join("+")
		this.setState({repository: value})
		//console.log(value)
	}

	renderResults(){
		console.log(this.state.data)
		if (this.state.data.total_count > 0) {
			return (this.state.data.items.map(item => this.returnTable(item)))
		} else {
			return(<p>Nenhum repositorio encontrado!</p>)
		}
	}

	onSavePressed(item){
		console.log(item)
	}
	returnTable(item){
		return(
			<Table>
				<thead>
				<tr>
				<th>#</th>
				<th>Score</th>
				<th>Name</th>
				<th>URL</th>
				</tr>
				</thead>
				<tbody>
				<tr>
				<td>1</td>
				<td>{item.score}</td>
				<td>{item.name}</td>
				<td><a href={item.url}>{item.url}</a></td>
				<td><button onClick={(item) => this.onSavePressed(item)}>Save</button></td>
				</tr>

				</tbody>
			</Table>
		)
	}

	render() {
	return (
		<div className="App">
			<div className="App-header">
				<img src={logo} className="App-logo" alt="logo" />
				<h2>Welcome to React</h2>
			</div>
			<div>
				Destaques
			</div>
			<div className="form">
				<form onSubmit={(event)=>{this.handleSubmit(event)}}>
					<label>
						What do you want to search for:<br />
						<input type="text" name="name" onChange={(event) => this.handleTextChange(event.target.value)} />
					</label>
					<br />
					<label>
						Choose a language:<br />
						<select value={this.state.language} onChange={(event) => this.handleSelectChange(event.target.value)}>
							<option value="python">Python</option>
							<option value="java">Java</option>
							<option value="html">HTML</option>
							<option value="css">CSS</option>
							<option value="javascript">JavaScript</option>
						</select>
					</label>
					<br /><br />
					<input type="submit" value="Search" />
				</form>
			</div><br /><br />

			<div className="table-responsive">
				{this.state.data ? this.renderResults() : null}
			</div>

		</div>
    );
  }
}

export default App;
